package com.uber;

import java.util.HashMap;
import java.util.Map;

class FindCircle {
	
	public static void main(String[] args) {
		String s ="";
		System.out.println(judgeCircle(s));
	}
    public static boolean judgeCircle(String args) {
        Map<Character, Integer> map = new HashMap<>();
        
        for(int i=0; i< args.length(); i++) {
            Character c = args.charAt(i);
            if(map.containsKey(c)) {
                map.put(c, map.get(c)+1);
            } else {
                map.put(c, 1);
            }
        }
        
      int uCount = ( map.get('U') == null) ? 0 : map.get('U');
      int dCount = ( map.get('D') == null) ? 0 : map.get('D');

      int rCount = ( map.get('R') == null) ? 0 : map.get('R');
      int lCount = ( map.get('L') == null) ? 0 : map.get('L');

        
        return uCount == dCount && rCount == lCount;
    }
}